export const accountsReducer = (state = {}, { type, payload }) => {
  console.log(type)
  switch (type) {
    case 'account':
      return { ...state, ...payload }
    case 'REMOVE_SELECTED_PRODUCT':
      return {}
    default:
      return state
  }
}
