import React from 'react'
import ContractAbi from '../../smart-contracts/build/contracts/ThoughtwinCoin.json'

import Web3 from 'web3'
const ThoughtWinContract = () => {
  const web3Contract = new Web3(Web3.givenProvider || 'localhost:8545')
  const contract = web3Contract.eth.contract(
    ContractAbi.abi,
    '0xAd09cA83A8fA444E429337a6B14E84C5130FC48f',
  )

  const account = web3Contract.getAccounts()

  const getBalance = async () => {
    const balance = await contract.methods.balanceOf(account[0])
    console.log(balance)
  }
  return (
    <div>
      <button onClick={getBalance}>getBalance</button>
    </div>
  )
}
export default ThoughtWinContract
